mapper.select:
 SELECT * FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY 



   ${sort} 



   b.publication_date DESC 

 ) AS rnum, b.isbn13, b.book, b.author, b.publisher, b.publication_date AS publicationDate, b.price, b.average_rating
AS averageRating, b.review_count, b.b_count, b.photo FROM book_tbl b 


   LEFT JOIN book_category_tbl bc ON b.isbn13 = bc.isbn13 LEFT JOIN category_tbl c ON bc.category_id = c.category_id 

 WHERE 1=1 


 GROUP BY b.isbn13, b.book, b.author, b.publisher, b.publication_date, b.price, b.average_rating, b.review_count, b.b_count, b.photo )
a WHERE rnum BETWEEN #{startRow} AND #{endRow}


SELECT c.category_id, c.category_name, c.category_level, c.parent_id
FROM book_category_tbl bc LEFT JOIN category_tbl c ON bc.category_id = c.category_id
WHERE bc.isbn13 = #{isbn13}

mapper.update:
 UPDATE book_tbl SET book = #{book}, book_description = #{bookDescription, jdbcType=CLOB}, price = #{price}, b_count = #{b_count},
average_rating = #{averageRating}, rental_available = #{rentalAvailable}, page_count = #{pageCount}, weight = #{weight}, book_size = #{bookSize},
star_1 = #{star_1}, star_2 = #{star_2}, star_3 = #{star_3}, star_4 = #{star_4}, star_5 = #{star_5}, review_count = #{review_count} WHERE isbn13 = #{isbn13}
