mapper.select:
 SELECT * FROM ( SELECT ROW_NUMBER() OVER ( ORDER BY 


   ${sort} 



   b.publicationDate DESC 


 ) AS rnum, b.isbn13, b.book, b.author, b.publisher, b.publicationDate AS publicationDate, b.price, b.averageRating
AS averageRating, b.reviewCount, b.bookCount, b.photo FROM book_tbl b 


   LEFT JOIN book_category_tbl bc ON b.isbn13 = bc.isbn13 LEFT JOIN category_tbl c ON bc.categoryId = c.categoryId 

 WHERE 1=1 


 GROUP BY b.isbn13, b.book, b.author, b.publisher, b.publicationDate, b.price, b.averageRating, b.reviewCount, b.bookCount, b.photo )
a WHERE rnum BETWEEN #{startRow} AND #{endRow}

mapper.update:
 UPDATE book_tbl SET book = #{book}, bookDescription = #{bookDescription, jdbcType=CLOB}, price = #{price}, bookCount = #{bookCount},
averageRating = #{averageRating}, rentalAvailable = #{rentalAvailable}, pageCount = #{pageCount}, weight = #{weight}, bookSize = #{bookSize},
star1Count = #{star1Count}, star2Count = #{star2Count}, star3Count = #{star3Count}, star4Count = #{star4Count}, star5Count = #{star5Count}, reviewCount = #{reviewCount} WHERE isbn13 = #{isbn13}
